public class Joining
{
  public static double join(double score)
  {
    if (score < 50)
    {
      score = score / 2;
      return score;
    }
    else
    {
      return 100.0;
    }
  }


  public static void main(String[] args)
  {
    System.out.print(join(6.2));
  }
}
